mapper.select:
select boardNumber, boardTitle, boardContent, b.userNumber, userId 
from tbl_board b join tbl_user u
on  b.userNumber = u.userNumber and boardNumber = #{boardNumber}

mapper.update:
update tbl_board
set readCount = readCount + 1
where boardNumber = #{boardNumber}

update tbl_board
set boardTitle=#{boardTitle}, boardContent=#{boardContent}
where boardNumber = #{boardNumber}

update tbl_reply 
set replyContent = #{replyContent} 
where replyNumber = #{replyNumber}
